mapper.select:
select route_name, route_id
from route where user_id =
#{userId}

select r.route_id, r.user_id, r.route_name, r.start_lat, r.start_lng, r.start_address, r.arrive_lat, r.arrive_lng, r.arrive_address, r.distance, r.time, s.step_id, s.sroute_id, s.vmode, s.step_name,
s.sstart_lat, s.sstart_lng,
s.send_lat, s.send_lng, s.route_distance, s.route_time, s.seq, v.vehicle_id, v.vstep_id, v.vstart_lat, v.vstart_lng, v.vstart_name, v.vend_lat, v.vend_lng, v.vend_name, v.headsign, v.vehicle_name, v.vehicle_num, v.vehicle_move from route r left join step s on r.route_id=s.sroute_id left join vehicle v on s.step_id=v.vstep_id where r.route_id = #{routeId}
